INSERT INTO `meeting_place` (`id`, `content`, `image`, `name`, `version`) VALUES
(1, '<p>\r\n	<span style="color:#21324E;font-family:����;line-height:21px;">凝聚着七千年中华古文明史的华夏之光壁画作为背景，给会场增添了浓浓的人文气息，展现了中华文明的博大精深与设计者的匠心独具。广场建造时配备了一流的灯\r\n光与音响设备。半圆型的壁画屏风结构具有良好的聚声、传音效果，成为举办露天音乐会的绝佳场所。适合举办大型文艺汇演和大型露天自助晚宴。\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<table border="1" width="90%">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<span>主要功能：</span>大型表演\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span>容纳人数：</span>广场可同时容纳5000人，就餐可容纳2000人\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span>功能面积：</span>13000平方米\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span>环境特点：</span>广场会所\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p style="text-align:center;">\r\n		<img style="width:448px;height:251px;" src="http://www.hy0773.com/attached/image/20120723/20120723112405_560.jpg" alt="" /><img src="http://www.hy0773.com/attached/image/20120723/20120723112416_189.jpg" alt="" /> \r\n	</p>\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '/attached/image/20120830/20120830145727_297.png', '桂林七星公园会所', 3),
(4, '<span style="color:#21324E;font-family:����;line-height:21px;">桂林山水甲天下，芦笛美景堪最佳。芦笛岩是桂林山水的一颗璀璨明珠。芦笛岩位于著名的旅游城市--桂林市的西北郊，距市中心5公里，是一个以游览岩洞为\r\n主、观赏山水田园风光为辅的风景名胜区，是我国负有盛名的旅游洞穴之一。芦笛岩洞深240米，游程500米。洞内有大量奇麓多姿、玲珑剔透的石笋、石乳、\r\n石柱、石幔、石花，琳琅满目，组成了狮岭朝霞、红罗宝帐、盘龙宝塔、原始森林、水晶宫、花果山等景观，令游客目不暇接，如同仙境，被誉为“大自然的艺术之\r\n宫”。从唐代起，历代都有游人踪迹，现洞内存历代壁画77则。自1959年发现并开发后，如今已建有餐厅、茶室、水榭、湖池、曲桥，并设游船，广植花木，\r\n成为中外游客游览桂林时必至的旅游热点。\r\n位于芦笛岩内的“水晶宫”冬暖夏凉，年平均温度为18度，此温度非常适合开会；水晶宫最宽处有93米， \r\n最高处18米，为芦笛岩内最广的地方，面积300平方米，可容150人，此空间大小正好适合开会及欣赏室内音乐，因为在此密闭的空间加上不规则的钟乳石壁\r\n为声音能被恰到好处地反射创造了最佳条件。水晶宫为芦笛岩景色的最精华所在， \r\n高阔的洞天、雄伟的气势、绚丽夺目的色彩，另人拍案叫绝。前联合国秘书长瓦尔德海姆赞美水晶宫“象联合国会议大厅，但比会议大厅更美丽。”\r\n<p>\r\n	<br />\r\n</p>\r\n<table border="1" width="90%">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<span>主要功能：</span>会议\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<span>容纳人数：</span>150人\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<span>功能面积：</span>300平方米\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<span>环境特点：</span>溶洞会所\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</span>', '/attached/image/20120830/20120830145909_350.jpg', '芦笛岩会所', 3),
(5, '<span style="color:#21324E;font-family:����;line-height:21px;">春天剧场主要在晚上用于表演及用餐表演。能容纳500人开会。灯光音响是一流的，也有会议的桌椅，能根据客户的要求摆不同的台型，完全具备接待会议的条\r\n件。春天剧场的位于榕湖湖畔，四周酒店众多，但桂林宾馆，榕湖饭店，景秀饭店等等酒店没有一家能容纳300人左右的会议室，只要达到300人以上的会议，\r\n只有桂山酒店的明华宫可以容纳得下，但桂山宾馆离市区较远，因此春天剧场对周围的酒店会议室是一个补充的作用，对于接待大型会议是很有竞争力的。\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src="http://www.hy0773.com/attached/image/20120720/20120720151556_252.png" alt="" />\r\n</p>\r\n<p>\r\n	<img style="width:526px;height:381px;" src="http://www.hy0773.com/attached/image/20120720/20120720151521_861.jpg" alt="" />\r\n</p>\r\n</span>', '/attached/image/20120830/20120830150012_487.jpg', '春天剧场会所', 2),
(6, '<span style="color:#21324E;font-family:����;line-height:21px;">李宗仁是桂林临桂人，33岁就当上了广西的省长，官职最高时为国民党代总统，李宗仁会所位于李宗仁官邸，能容纳30—40人开会，此会所是桂系最高级别军官开会的会议室，&nbsp;目前此会议室连同于李宗仁官邸作为供认参观的场所，有极高的参观价值。&nbsp; <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 此会议室能适合开董事会会议，公司高层领导会议等等，另外此会议室是桂林特有的宝贵资源及历史文物古迹，而且全国也没有类似可租用的会议场所。\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src="http://www.hy0773.com/attached/image/20120720/20120720151122_566.png" alt="" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style="text-align:center;">\r\n	<img style="width:485px;height:322px;" src="http://www.hy0773.com/attached/image/20120720/20120720151028_101.jpg" alt="" /> \r\n</p>\r\n</span>', '/attached/image/20120830/20120830150121_705.jpg', '桂林“总统”会所', 1),
(7, '<span style="color:#21324E;font-family:����;line-height:21px;">两江四湖是桂林最具特色的旅游路线，两江四湖游船会所根据会议的时间及人数来包租两江四湖的游船，可根据客人的具体情况设计路程，随时客人可以停在他想停\r\n的地方，总之给客户美丽的移动的封闭的安全的会议场所。客人可以靠岸做团队游戏，也可以举办鸡尾酒会，也可以在船上欣赏富有民族特色乐器表演。\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src="http://www.hy0773.com/attached/image/20120720/20120720120044_873.png" alt="" /> \r\n</p>\r\n<p style="text-align:center;">\r\n	<img src="http://www.hy0773.com/attached/image/20120720/20120720115951_181.jpg" alt="" /> \r\n</p>\r\n</span>', '/attached/image/20120830/20120830150324_838.jpg', '两江四湖游船会所', 1);
INSERT INTO `metting_service` (`id`, `content`, `version`) VALUES
(1, '<span style="color:#21324E;font-family:����;line-height:25px;">桂林是一座美丽的旅游城市，桂林山水风景如画，交通便利，旅业发达，星级酒店便布城区，中外游客云集，在此举办会议会展，让您感觉非同，永不忘怀。如果您准备将会议、会展安排在广西桂林举办，只要把您的要求告诉我们，剩下的一切由我们为您安排。我们业务范围包括：&nbsp;</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（1）会议酒店：各档桂林星级酒店预订...</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（2）会议场地：各种会议室场所及会场布置...&nbsp;</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（3）会议用餐：桂林地方特色风味餐...&nbsp;</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（4）会议用车：各款中低高档轿车，巴士...</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（5）会议票务：代订返程火车票...</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（6）旅游考察：提供桂林旅游阳朔旅游线路...</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（7）会议娱乐：各种会后娱乐活动...</span><br />\r\n<span style="color:#21324E;font-family:����;line-height:25px;">（8）会议礼品：桂林会议旅游纪念品订作...</span>', 0);
