INSERT INTO `scenic` (`id`, `content`, `door_price`, `image`, `name`, `version`, `address`, `strength`, `sort`, `description`, `keywords`) VALUES
(1, '<p style="background:#FAFAFA;">\r\n	<span style="font-family:楷体_GB2312;"><span style="font-size:14.0pt;font-family:楷体_GB2312;"><span style="font-size:14px;"></span> </span></span> \r\n</p>\r\n<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:楷体_GB2312;"><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;"> </span></b></span></b> \r\n</p>\r\n<b> \r\n<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">把桂林市中心区的漓江、桃花江、杉湖、榕湖、桂湖、木龙湖贯通，形成环城游览水系，即“两江四湖”工程。这是一个富有激情、富有想象力的工程，是桂林历史上最大的环保工程。今之两江四湖，已为甲天下桂林山水锦上添花，是令中外游客流连忘返旅游景点。</span><span></span></span></b> \r\n</p>\r\n<br />\r\n<span></span></b> \r\n<p>\r\n	<br />\r\n</p>\r\n<br />\r\n<span></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<br />', 190, '/attached/image/20120823/20120823152851_406.png', '两江四湖', 14, 1, 2, 5, NULL, NULL),
(2, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">芦笛岩位于桂林市西北郊，距市中心</span><span style="font-size:16px;"> 5 </span><span style="font-size:16px;">公里，是一个以游览岩洞为主、观赏山水田园风光为辅的风景名胜区。芦笛岩洞深</span><span style="font-size:16px;"> 240 </span><span style="font-size:16px;">米，游程</span><span style="font-size:16px;"> 500 </span><span style="font-size:16px;">米。洞内有大量奇麓多姿、玲珑剔透的石笋、石乳、石柱、石幔、石花，琳琅满目，组成了狮岭朝霞、红罗宝帐、盘龙宝塔、原始森林、水晶宫、花果山等景观，令游客目不暇接，如同仙境，被誉为\r\n“ 大自然的艺术之宫 ” 。从唐代起，历代都有游人踪迹，现洞内存历代壁画</span><span style="font-size:16px;"> 77 </span><span style="font-size:16px;">则。自</span><span style="font-size:16px;"> 1959 </span><span style="font-size:16px;">年发现并开发后，如今已建有餐厅、茶室、水榭、湖池、曲桥，并设游船，广植花木，成为中外游客游览桂林时必至的旅游热点。</span><span></span></span></b> \r\n</p>\r\n<br />', 90, '/attached/image/20120820/20120820104349_635.jpg', '芦笛岩', 4, 1, 2, 2, NULL, NULL),
(3, '<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;"><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">象鼻山位于桂林市内桃花江与漓江汇流处<span>, </span>是桂林名山之一；主要景点有水月洞、象眼岩、普贤塔、宏峰寺及寺内的太平天国革命遗址陈列馆等。附近还有隋唐开元寺仅存的舍利塔；水月洞紧靠江边<span>, </span>漓水流贯其间<span>, </span>如水中浮月<span>,</span>山石垂入水中又如象鼻饮水漓江<span>, </span>景致极佳<span>, </span>唐宋以来即为游览胜地。宋蓟北处士《和水月洞韵》诗： “水底有明月<span>, </span>水上明月浮。水流月不去<span>, </span>月去水还流。”形象地描绘了这一景致；是中国首批<span>4A</span>级景区，位于桂林市滨江路，桃花江与漓江的交汇处，<span>2</span>、<span>23</span>、<span>16</span>、<span>57</span>、<span>58</span>、<span>88</span>路公交车可到达；占地<span>11.88</span>万平方米，属于喀斯特地貌自然风景区，因山形酷似一匹豪饮江水的巨象而得名。有<span>1000</span>多年的游览史。</span></b></span></b>', 75, '/attached/image/20120820/20120820105835_462.png', '象鼻山', 4, 1, 1, 1, NULL, NULL),
(4, '<p>\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">叠彩山旧名桂山，位于桂林市区东北部，滨临漓江。叠彩山与城中的独秀峰、漓江畔的伏波山鼎足而立，同为城内的游览胜地。叠彩山占地面积约<span>2</span>平方公里，由明月峰、仙鹤峰和四望山、于越山组成，横亘市区，景色优美，又易于攀登，为桂林山景中的一个热点。山中佳景甚多，有叠彩亭、于越阁、瞿张二公成仁碑、仰止堂、仙鹤洞、风洞、叠彩楼、望江亭和拿云亭等名胜。山上历代名人的摩崖石刻尢多，为文物的精华。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 35, '/attached/image/20120820/20120820111016_736.jpg', '叠彩山', 4, 1, 1, 8, NULL, NULL),
(5, '<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">伏波山位于市区东北，滨江路北端与风北路交界处，孤峰突起，西枕陆地，东临漓江，有遏波伏澜之势，因唐代曾在山上修建汉朝伏波将军马援祠而得名。伏波山公园由多级山地庭园组成，有还珠洞、千佛岩、珊瑚岩、试剑石、听涛阁、半山亭、千人锅及大铁钟等景点和文物，集山、水、洞、石、亭、园、文物于不足<span>1</span>万平方米的范围内，成为独特的桂林山水的缩影。</span></b>', 30, '/attached/image/20120820/20120820112810_722.jpg', '伏波山', 3, 1, 1, 7, NULL, NULL),
(6, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">桂林经典刘三姐大观园原名桂林刘三姐景观园，坐落在风景怡人的桃花江畔，占地一百多亩，总投资伍仟余万元人民。刘三姐大观园是桂林市旅游业的一颗新星，她以刘三姐文化精华为主题，集观赏、娱乐为一体的休闲游乐场所。</span><span></span></span></b> \r\n</p>', 90, '/attached/image/20120820/20120820114556_754.jpg', '刘三姐大观园', 3, 1, 1, 6, NULL, NULL),
(7, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">位于桂林城南郊， 以穿山为轴心， 占地面积约</span><span style="font-size:16px;">2</span><span style="font-size:16px;">平方公里， 是桂林市山水旖旎的公园之一。穿山隔着漓江与象鼻山相望， 与江西岸的龟山， 形如两只相斗的公鸡， 合称斗鸡山。穿山前有塔山，\r\n山顶有明代寿佛塔。小东江在两山之间蜿蜒流过， 山倒影江中， 景色尤佳。穿山有月岩、穿岩。月岩位于山腰， 洞穿山壁， 高广空明， 宛如月宫仙境， 站在洞中南北远眺，\r\n但见漓水蜿蜒， 风光无限。穿岩位于山底， 长</span><span style="font-size:16px;">500</span><span style="font-size:16px;">余米， 洞内除石钟乳外， 尚有罕见的透明结晶体——石枝、鹅管等，\r\n为岩溶地貌奇景。穿山岩是桂林近年开放的第三个大型溶洞。</span><span></span></span></b> \r\n</p>', 65, '/attached/image/20120820/20120820143741_243.jpg', '穿山', 2, 1, 2, 10, NULL, NULL),
(8, '<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">日月双塔地处桂林市中心区，坐落在桂林城的中轴线上，与“象山水月”相邻。象山是桂林的城徽，而双塔则是新桂林的标识。日塔、月塔与象山上的普贤塔、塔山上的寿佛塔，相互呼应，相互映衬，有“四塔同美”之说。 <span>&nbsp;</span><span></span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">桂林日月双塔号称世界第一铜塔，是桂林旅游的一个绝美夜景观赏地。地处桂林市中心区，座落在桂林城的中轴线上，与“象山水月”相邻。象山是桂林的城徽，而双塔则是新桂林的标识。其中日塔高<span>41</span>米共九层，通体均为纯铜装饰，耗用铜材<span>350</span>吨，金碧辉煌，并有电梯供游客观光乘坐。铜塔所有构件如塔什、瓦面、翘角、斗拱、雀替、门窗、柱梁、天面、地面完全由铜壁画装饰，整座铜塔创下了三项世界之最——世界上最高的铜塔，世界上最高的铜质建筑物，世界上最高的水中塔。\r\n月塔高<span>35</span>米共七层，为琉璃塔。每层的雕花彩绘门窗寓意不同的主题，富含中国传统韵味，通过水下<span>18</span>米长的水族馆与日塔连接，其地宫之中有桂林明代青花梅瓶大型壁画。杉湖日月双塔已成为观赏桂林城市精华美景的胜地。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 30, '/attached/image/20120820/20120820150616_81.jpg', '日月双塔', 2, 1, 2, 3, NULL, NULL),
(9, '<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">七星公园<span> - </span>现代城市公园拥有多样化的生态环境及服务设施<span>,</span>具有休闲游憩、旅游观光等多种功能。七星景区位桂林漓江东岸，漓江支流小<span><a href="http://baike.baidu.com/view/204246.htm" target="_blank"><span style="color:black;text-decoration:none;"><span>东江</span></span></a></span>畔，距市区<span>1.5</span>公里，因七星山七星岩而得名，是桂林市最大的综合性的生态景区，占地面积约<span>134.7</span>公顷，集山青、水秀、洞奇、石美于斯，俨然成为桂林山水的缩影，自隋唐时期起就已成为游览胜地，如今更成为世界旅游组织推荐景区、中国首批<span>AAAA</span>级景区、国家重点名胜风景区、桂林的核心景区。此外，一千多年以来，中国四大宗教信仰佛教、道教、伊斯兰教、儒家学说曾先后影响过景区，给这里印下了鲜明的宗教文化烙印，赋予了景区独特的“宗教四合一”的魅力。</span></b>', 35, '/attached/image/20120820/20120820145844_311.jpg', '七星公园', 2, 1, 2, 9, NULL, NULL),
(10, '<p style="text-indent:24.1pt;">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">冠岩景区是桂林市新开辟的游览区。位于桂林市南<span>29</span>公里处，漓江东岸的草坪乡，因山形如帝王的紫金冠而得名，冠岩是一个巨型地下河溶洞，全长<span>12</span>公里，发源于桂林东面的海洋山。它的独特游览方式一直吸引着广大的游客。先是从半山腰入洞，乘坐<span>30</span>多米高的观光电梯直入洞内，仿佛坠入瑶池一般，然后在景观奇异的洞穴中游览，不一会来到了洞内有轨电车旁，乘上小电车，穿过神秘变幻的地下峡谷，几分种后便到了气势恢宏的棕榈树大厅，这里琳琅满目的石钟乳令您感觉走进了巨人国的热带雨林，是桂林诸岩洞的一大奇观。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b>\r\n</p>', 80, '/attached/image/20120820/20120820162615_11.png', '冠岩', 0, 1, 2, NULL, NULL, NULL),
(11, '<p style="text-align:left;text-indent:24.0pt;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">桂林古东瀑布是可以攀爬的瀑布，位于桂林至冠岩旅游线上，是桂林景区开发的后起之秀。桂林古东瀑布景区是以地下涌泉汇集形成的多级串连瀑布，瀑布比漓江水位还高<span>180</span>米，尚能喷涌、是全国唯一一个由地下涌泉来形成的多级串连瀑布；是一个因钙活华沉积作用可逐渐长高而改变景致的瀑布；九级瀑布形态各异，有的如鸳鸯戏水，有的如蛟龙喷水，有的在长满青苔的岩壁上，瀑水如布，水纹清晰可鉴，呈白色透明状，尤如喷雾行云；有的水流集中下注跌入深潭溅起层层水雾浪花，尤如细雨蒙蒙，瀑布两岸林木葱郁，荫蔽幽静，水质清洌透明，凉爽甘甜。<span></span></span></b>\r\n</p>', 60, '/attached/image/20120820/20120820163953_953.jpg', '古东瀑布', 0, 1, 1, NULL, NULL, NULL),
(12, '<b><span style="font-size:10.5pt;font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">桂林荔浦银子岩旅游景区位于桂林市荔浦县马岭镇荔桂公路东侧，</span><span style="font-size:16px;">321</span><span style="font-size:16px;">国道旁，距桂林市中心</span><span style="font-size:16px;">85</span><span style="font-size:16px;">公里，距中国旅游名县阳朔县界</span><span style="font-size:16px;">3</span><span style="font-size:16px;">公里，占地面积</span><span style="font-size:16px;">1500</span><span style="font-size:16px;">亩。</span><span style="font-size:16px;">1999</span><span style="font-size:16px;">年初对外开放，是国家</span><span style="font-size:16px;">AAAA</span><span style="font-size:16px;">级景区、首批“桂林市文明旅游风景区示范点”，是桂林旅游圈新出现的一颗璀璨明珠，也是桂林现较大的</span><span><a href="http://baike.baidu.com/view/1615232.htm" target="_blank"><span style="color:black;font-size:16px;"><span style="font-size:16px;">岩洞</span></span></a></span><span style="font-size:16px;">。</span><span><span style="font-size:16px;">银子岩</span><span><a href="http://baike.baidu.com/view/111456.htm" target="_blank"><span style="color:black;font-size:16px;"><span style="font-size:16px;">溶洞</span></span></a></span><span style="font-size:16px;">是典型的</span><span><a href="http://baike.baidu.com/view/4101.htm" target="_blank"><span style="color:black;font-size:16px;"><span style="font-size:16px;">喀斯特地貌</span></span></a></span><span style="font-size:16px;">，贯穿十二座山峰，属层楼式溶洞，洞内汇集了不同地质年代发育生长的</span><span><a href="http://baike.baidu.com/view/122605.htm" target="_blank"><span style="color:black;font-size:16px;"><span style="font-size:16px;">钟乳石</span></span></a></span><span style="font-size:16px;">，晶莹剔透，洁白无瑕，宛如夜空的银河倾斜而下，闪烁出像银子、似钻石的光芒，所以称为“银子岩”。</span></span><span style="font-size:16px;">有俗话说：“游了银子岩，一世不缺</span><span style="font-size:16px;">钱</span><span style="font-size:16px;">”之称</span><span style="font-size:16px;">。</span></span></b>', 65, '/attached/image/20120820/20120820165516_612.jpg', '银子岩', 0, 1, 1, NULL, NULL, NULL),
(13, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;"><span style="font-size:16px;">世外桃源属于国家</span><span style="font-size:16px;">AAAA</span><span style="font-size:16px;">级景区，距桂林</span><span style="font-size:16px;">44</span><span style="font-size:16px;">公里，距阳朔</span><span style="font-size:16px;">16</span><span style="font-size:16px;">公里，交通十分方便。景区主要</span></span></b><b><span style="font-family:仿宋_GB2312;"><span style="font-size:16px;">有荷花池、燕子湖、燕子洞、侗乡风情和原始部落组成。若是“沾衣欲湿杏花雨，吹面不寒杨柳风”的季节．踏进“世外桃源”，展现在眼前的将是一片秀美的山水田园风光。清波荡漾的燕子湖镶嵌在大片的绿野平畴之中，宛如少女的明眸脉脉含情。湖岸边垂柳依依，轻拂水面。一架巨大的水转筒车，吱吱呀呀地摇着岁月，也吟唱乡村古老的歌谣。放眼望去，远方群山，村树含烟，阡陌纵横，屋宇错落，宛若陶渊明笔下“芳草鲜美，落英缤纷”“有良田美池桑竹之属”的桃源画境。</span><span></span></span></b>\r\n</p>', 70, '/attached/image/20120820/20120820171422_399.jpg', '世外桃源', 0, 1, 3, NULL, NULL, NULL),
(14, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">阳朔月亮山位于</span><span><a href="http://baike.baidu.com/view/32061.htm" target="_blank"><span style="color:black;font-size:16px;"><span style="font-size:16px;">大榕树</span></span></a></span><span style="font-size:16px;">南</span><span style="font-size:16px;">0.5</span><span style="font-size:16px;">公里处，高</span><span style="font-size:16px;">230</span><span style="font-size:16px;">米，山顶石壁如屏，当中一洞穿透，宛如明月高挂碧空，故名月亮山。从山脚有直通山洞的蜿蜒小道，游人拾</span><span style="font-size:16px;">800</span><span style="font-size:16px;">级可达“月洞”，洞高</span><span style="font-size:16px;">50</span><span style="font-size:16px;">米，洞内有石似玉兔、嫦娥、吴刚，皆为天然。洞西北侧有一略低于月亮山的圆顶状山峰，因此，沿同脚赏月路不同角度观看月洞，步移景换，月随人变，显现圆月、半月、眉月的不同景象，观赏到月亮“由圆变缺，由缺高圆”的盈缺变化，如此惟妙惟肖地宛若明月高悬的天生景观，世界上惟有此处。</span></span></b><b><span style="font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 15, '/attached/image/20120823/20120823162623_824.png', '月亮山', 2, 2, 1, 8, NULL, NULL),
(15, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">大榕树位于阳朔县城南</span><span style="font-size:16px;">7.5</span><span style="font-size:16px;">公里。</span></span></b><b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">是一棵千年古树，</span><span><span style="font-size:16px;">树高</span><span style="font-size:16px;">17</span><span style="font-size:16px;">米，树围</span><span style="font-size:16px;">7.05</span><span style="font-size:16px;">米，远望似一绿色巨伞撑立地上。隔河有穿岩，组成穿岩古榕景点。阳朔大榕树景区是世界岩溶喀斯特地貌峰林峰丛最集中、最典型、最有代表性的地方，以奇特的岩溶喀斯特地貌、如诗如画般的山水田园风光和丰富的壮族少数民族民俗文化闻名天下。诗情画意韵律优美的稻田，清澈见底的小河，青翠湿润的山峰，凤尾竹，壮家村寨，水车，清新爽人的空气，标示着这里人与自然的完美和谐，更似一幅艺术水平极高的中国水墨山水画长卷。曾被美国哈佛大学博士、世界著名园林专家卡尔称为：在中国看到了最美丽的地方。风靡中国及东南亚地区的电影《刘三姐》很多镜头在此拍摄，影片中刘三姐与阿牛哥大榕树下抛绣球定终身的情景打动了天下有情人。</span></span><span></span></span></b>\r\n</p>', 18, '/attached/image/20120821/20120821094932_480.jpg', '大榕树', 0, 2, 2, 10, NULL, NULL),
(16, '<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">乐满地主题乐园集当今世界人文景观之大成，中西合壁、古今纵横，辟有美国大西部、中国城、梦幻世界、港口水上游乐区等景观。同时，引进了“激流泛舟”、“飞艇冲浪”、“疯狂巴士”、“云霄飞车”、“过山车”、“海盗船”等惊险刺激的游乐设施。整个园区可观、可游、可疯狂、可闲逸<span>...... </span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">乐园五大主题区： <span></span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">欢乐中国城：音乐马车、动感影院、吉祥飞龙 <span></span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">海盗村：海盗船、醉酒桶、高空弹跳、声光游乐城 <span></span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">美国西部区：急流泛舟、神枪手、西部牛仔秀、小马牧场、金矿山 <span></span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">梦幻世界区：鬼屋惊魂、大航海家、妙秒车、碰碰车、疯狂巴士、欢乐剧场 <span></span></span></b> \r\n</p>\r\n<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">南太平洋区：霹雳过山车、飞艇冲浪、划水特技、魔术喷泉</span></b>', 110, '/attached/image/20120821/20120821100533_43.jpg', '乐满地主题乐园', 1, 4, 1, 11, NULL, NULL),
(17, '<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">龙脊梯田距龙胜县城<span>22</span>公里，距桂林市区<span>103</span>公里，景区面积共<span>66</span>平方公里，梯田分布在海拔<span>300</span>至<span>1100</span>米之间，坡度大多在<span>26</span>至<span>35</span>度之间，最大坡度达<span>50</span>度。从山脚盘绕到山顶，小山如螺，大山似塔，层层叠叠，高低错落。从高处望去，梯田的优美曲线一条条、一根根、或平行或交叉，蜿蜒如春螺、披岚似云塔，显示了动人心魄的曲线美。其线条行云流水，潇洒柔畅；其规模磅礴壮观，气势恢宏，有“梯田世界之冠”的美誉。</span></b>', 80, '/attached/image/20120821/20120821102826_966.jpg', '龙脊梯田', 0, 3, 1, 12, NULL, NULL),
(18, '<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">蝴蝶泉位于桂林阳朔月亮山风景区“十里画廊”的精华旅游地段，源于景区内一岩洞中有一酷似蝴蝶的钟乳石而得名。是中国目前最大的活蝴蝶观赏园，景区里有上千种数万只蝴蝶与人和谐相处，亲切、自然，是一个生态旅游风景点；且集奇山、秀水、幽洞、田园风光为一体，有“不到蝶山顶，不知阳朔景”之美誉，是阳朔山水的典型代表。蝴蝶泉园区内主要可观景点有：蝶洞、蝶山瀑布、蝶桥、蝶山胜景、蝶缘、蜂缘、蝴蝶馆等。在蝴蝶泉，除可观赏到阳朔田园风光外，还可见地方风情特色表演、欣赏训蜂绝技、免费品尝野生蜂蜜。特别值得一提的是园内的蝶缘。她是目前中国最大的蝴蝶园，斑斓的蝴蝶，翩翩起舞于万木丛中，只要走进蝶缘，您会情不自禁地也要“寻花问柳、招风惹蝶”，体验“与蝶共舞、蝶蝶不休”的感受。登临蝶山顶，就到达俯瞰阳朔田园风光的最好地点——或碧空如洗，或烟雨朦胧，或一抹夕阳下，薄雾笼罩的山村升起的袅袅炊烟……<span></span></span></b>\r\n</p>', 70, '/attached/image/20120821/20120821105311_641.png', '蝴蝶泉', 0, 2, 2, 14, NULL, NULL),
(19, '<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">龙潭位于马山脚下大榕树南<span>0.3</span>公里，岩内钟乳石琳琅多姿，有石巧似游龙戏水；岩外青山隐隐，绿水悠悠，宛如群龙驾雾，故名“聚龙潭”。聚龙潭内，观赏的内容全是溶洞景观。一般溶洞所有的石芽、石花、钟乳、穴珠、瀑布、落水洞、地下河等等皆可见到，美不胜收。有些地段移步换景，令人目不暇接。且由这些美景所附今流传的一个个优美神话，或启迪智慧、或嘲骂邪恶、或赞颂爱情、或宣扬中华传统美，皆优美动人。游岩归来，不仅能让人获得美的享受，也会得到一些新的知识。 <span></span></span></b> \r\n</p>\r\n<p style="text-align:left;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">　　聚龙潭又是阳朔境内唯一可兼水陆游览的溶洞。在近千米长的游道中，有约四分之一的游程游人可乘船游览。水道旁又设有安全防护栈道连通。水陆游程，皆可随游客自己选择。<span></span></span></b> \r\n</p>', 45, '/attached/image/20120821/20120821114123_416.png', '聚龙潭', 1, 2, 2, 15, NULL, NULL),
(20, '<p style="text-align:left;text-indent:25.5pt;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">遇龙河是阳朔仅次于漓江的第二大河流。发源于临桂县古里河，流经阳朔三乡镇三十余个村庄，沿桂（林）阳（朔）公路<span>(</span>国道<span>321</span>线<span>)</span>走向缓缓南流。至穿岩古榕景区青厄渡口处与金宝河汇合后流入漓江。遇龙河全长<span>43.5</span>公里，宽约<span>38</span>—<span>61</span>米，深<span>0.5</span>—<span>2</span>米长年河水清澈，水流缓慢。正常流量约每秒<span>5</span>立方米。河中筑有多处堤坝；不通航，素有“小漓江”之称，是阳朔县部分重点文物古迹和主要田园山水风景所在地。广西第一古桥——仙桂桥，广西最大单孔石桥——遇龙桥，阳朔最圆单孔石桥——富里桥。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 150, '/attached/image/20120821/20120821115548_319.jpg', '遇龙河', 1, 2, 2, 16, NULL, NULL),
(21, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:16px;">尧山位于桂林市东郊，距市中心</span><span style="font-size:16px;">8</span><span style="font-size:16px;">公里，主峰海拔</span><span style="font-size:16px;">909.3</span><span style="font-size:16px;">米，相对高度</span><span style="font-size:16px;">760</span><span style="font-size:16px;">米，是桂林市最高的山峰。因周唐时在山上建有尧帝庙而得名。尧山风景区是国家首批</span><span style="font-size:16px;">AAAA</span><span style="font-size:16px;">级旅游风景区和桂林市首批文明风景旅游示范点。</span><span style="font-size:16px;">尧山以变幻莫测、绚丽多彩的四时景致闻名于世，被淮为欣赏桂林山水的最佳去处。尧山还是一个风水宝地，有全国保存最完整的明代藩王墓群靖江王陵，规模宏大辉煌，在此出土的梅瓶名扬四海。</span><span></span></span></b>\r\n</p>', 110, '/attached/image/20120821/20120821160454_634.jpg', '尧山景区', 0, 1, 1, 17, NULL, NULL),
(22, '<p style="text-align:left;text-indent:25.5pt;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">大型桂林山水实景演出《印象·刘三姐》是中国·漓江山水剧场之核心工程，由桂林广维文华旅游文化产业有限公司投资建设、我国著名导演张艺谋、王潮歌、樊跃出任总导演，国家一级编剧、中国实景演出创始人、山水文化机构董事长梅元帅任总策划、制作人，历时五年半努力制作完成。它集漓江山水、广西少数民族文化及中国精英艺术家创作之大成，是全国第一部全新概念的“山水实景演出”。演出集唯一性、艺术性、震撼性、民族性、视觉性于一身，是一次演出的革命、一次视觉的革命。印象·刘三姐是全球最大的山水实景剧场；全国第一部全新概念实景演出；艺术性、震撼性、民族性、视觉性的集合、历经<span>5</span>年零<span>5</span>个月；<span>1 .654</span>平方公里水域、<span>12</span>座著名山峰、<span>61</span>位中外著名艺术家参与创作、<span>109</span>次修改演出方案、<span>600</span>多名演职人员参加演出。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 198, '/attached/image/20120821/20120821165208_323.jpg', '印象刘三姐', 0, 2, 3, 18, NULL, NULL),
(23, '<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:14px;">&nbsp;&nbsp; 漓江位于华南广西壮族自治区东部，属珠江水系。漓江发源于“华南第一峰”桂北越城岭\r\n漓江见闻猫儿山，那是个林丰木秀，空气清新，生态环境极佳的地方。漓江上游主流称六峒河；南流至兴安县司门前附近，东纳黄柏江，西受川江，合流称溶江；由溶江镇汇灵渠水，流经灵川、桂林、阳朔，至平乐，汇入西江，全长</span><span style="font-size:14px;">437</span><span style="font-size:14px;">公里。从桂林到阳朔约</span><span style="font-size:14px;">83</span><span style="font-size:14px;">公里的水程，称漓江。</span><span></span></span></b> \r\n</p>\r\n<p style="background:#FAFAFA;">\r\n	<b><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:14px;">&nbsp;&nbsp; 漓江是世界上风光最秀丽的河流之一，长</span><span style="font-size:14px;">160</span><span style="font-size:14px;">公里。漓江两岸的山峰伟岸挺拔，形态万千，石峰上多长有茸茸的灌木和小花，远远看去，若美女身上的衣衫。江岸的堤坝上，终年碧绿的凤尾竹，似少女的裙裾，随风摇曳，婀娜多姿。最可爱是山峰倒影，几分朦胧，几分清晰。江面渔舟几点，红帆数页，从山峰倒影的画面上流过，真有“船在青山顶上行”的意境。百里漓江的每一处景致，都是一幅典型的中国水墨画。漓江自桂林至阳朔</span><span style="font-size:14px;">83</span><span style="font-size:14px;">公里水程，是广西东北部喀斯特地形发育最典型的地段。</span></span></b><b><span style="font-family:仿宋_GB2312;color:black;"></span></b> \r\n</p>', 210, '/attached/image/20120823/20120823152235_302.png', '漓江', 2, 1, 2, 17, NULL, NULL),
(24, '<p style="text-align:left;text-indent:25.5pt;background:#FAFAFA;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">兴坪渔村是一个古老的村庄，传说为兴坪圩上达官显贵为择贵地。从兴坪集镇码头沿漓江相去<span>2</span>公里，便是中国第一位总统孙中山、美国总统克林均访问过的兴坪渔村，兴坪渔村处漓江边，建于公元<span>1506</span>年，传说为兴坪圩上达 官显贵为择贵地迁此。村中房屋与圩上古建筑风格相近，青砖黑瓦，坡屋面、马头墙、飞檐、画栋、雕花窗、鳞次栉比，结构独特，具有典型的明清暑期桂北民居特\r\n色，距今近<span>500</span>年历史，仍保存完好。游览兴坪渔村和坐船游漓江精华段，大约时间为<span>90</span>分钟。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b>\r\n</p>', 130, '/attached/image/20120823/20120823155818_5.jpg', '兴坪渔村', 0, 2, 3, 19, NULL, NULL),
(25, '<p style="text-align:left;text-indent:25.5pt;background:#FAFAFA;" align="left">\r\n	<strong><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">桂林市区水上游</span></strong><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;">（又名桂林市内漓江水上游，十大名山水上游），乘船可以观赏桂林十大名山桂林城徽—象鼻山、半枕漓江—伏波山、“江山汇景处”—叠彩山、仰首挺胸的—斗鸡山、一轮明月挂天空的—穿山、天然屏障的—南溪山、与之隔江相望的—龟山、两山之间的—塔山、北斗七星的—七星山、老人山、骆驼过江、鲤鱼拦江等景点。坐船游览桂林市区风景更加休闲方便，不会像陆地游览那样一个个景区爬山那么累。桂林老人山在晚霞的映衬下显得格外的美丽，最美的风光不一定是要在公园，不一定要到身临其境，也许最美丽的风景也可以远观。</span></b><b><span style="font-size:12.0pt;font-family:仿宋_GB2312;color:black;"></span></b>\r\n</p>', 65, '/attached/image/20120823/20120823162156_365.jpg', '桂林市区水上游', 0, 1, 3, 19, NULL, NULL),
(26, '<p style="text-align:left;text-indent:21.0pt;" align="left">\r\n	<span style="font-family:仿宋_GB2312;color:black;">靖江王府是明代靖江藩王府地遗址，南北长<span>556</span>米，东西宽<span>355</span>米，占地面积<span>19.78</span>万平方米，城池始建于<span>1372</span>年，其城垣全部采用巨型方整的石料砌成，城墙厚<span>5.5</span>米，高近<span>8</span>米。从建成到明朝灭亡的<span>257</span>年里，这里共住过<span>11</span>代共<span>14</span>位藩王，于清顺治九年（<span>1652</span>）被焚毁。后又建贡院于此。<span>1921</span>年孙中山北伐时将这里作为大本营。民国初年，这里先后建成了第二师范学校，模范小学，第三高级中学，甲种工业学校校址；<span>1925</span>年又辟做中山公园；<span>1937</span>年成为广西省府。抗日期间再度被毁，重建后成为广西师范大学校址。</span><span style="font-family:Arial;color:black;">&nbsp;</span><span style="font-family:仿宋_GB2312;color:black;"></span>\r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:仿宋_GB2312;color:black;">整个王城的宫殿建筑采取严格的中轴对称布局方式营造，中轴线上的承运门、承运殿，高大华丽，气势非凡，而轴线两侧的建筑则矮小简单。古代的建筑师正是利用这种明显的反差，来体现王权的至高无上。承运殿左侧为祖庙，是王室拜祭祖先的地方。设在左边表示把对祖先的崇敬放在第一位；右为社稷坛，是王室祭神祈福的地方。承运殿后便是靖江王的寝宫。我国的宫殿大都按照这种“左祖右社、前朝后寝”的布局建造。王城南面正阳门上，有清代两广总督为表彰连中“三元”（解元、会元、状元）的桂林士子陈继昌而立的“三元及第”坊；东华门上的“状元及第”坊则是为道光年间新科状元龙启瑞而建；西华门上的“榜眼及第”坊是为同治年间永福人于建章而建。</span><span style="font-family:仿宋_GB2312;color:black;"></span>\r\n</p>', 70, '/attached/image/20120903/20120903095455_663.jpg', '靖江王府', 0, 1, 2, 15, NULL, NULL),
(27, '<p style="text-align:left;text-indent:22.2pt;" align="left">\r\n	<span style="font-family:仿宋_GB2312;color:black;font-size:14px;">十二滩漂流</span><span style="font-family:仿宋_GB2312;color:black;"><span style="font-size:14px;">景区位于桂林市临桂县宛田乡东北部，桂林至龙胜黄金旅游线上。漂流河段位于桂林市临桂县义江上游，漂流终点距桂林市只有</span><span style="font-size:14px;">50</span><span style="font-size:14px;">公里，北至龙胜约</span><span style="font-size:14px;">30</span><span style="font-size:14px;">公里，交通十分便利。十二滩漂流是中国专业漂流旅游胜地、\r\n荣获国家</span><span style="font-size:14px;">3A</span><span style="font-size:14px;">景区、广西首推大峡谷自助漂荣誉。 十二滩河谷两岸是高耸入云的山崖和叠障的梯田，从山脚到山顶分布着苍翠茂密的森林。漂流河道全长</span><span style="font-size:14px;">9.5</span><span style="font-size:14px;">公里，总落差近</span><span style="font-size:14px;">100</span><span style="font-size:14px;">米。十二滩漂流以一段近</span><span style="font-size:14px;">2000</span><span style="font-size:14px;">米十二个急滩相连而得名，其中急滩中最高单个落差达</span><span style="font-size:14px;">3</span><span style="font-size:14px;">米，景区内平均海拔为</span><span style="font-size:14px;">300</span><span style="font-size:14px;">至</span><span style="font-size:14px;">500</span><span style="font-size:14px;">米，气候垂直差异较大，四季分明，雨量充沛，盛夏时气温比桂林市区低</span><span style="font-size:14px;">3</span><span style="font-size:14px;">至</span><span style="font-size:14px;">5</span><span style="font-size:14px;">摄氏度。在峡谷两岸连绵的山崖间悬挂着许多小瀑布，十分清幽秀美，使人仿佛置身于仙境之中。当地有一句俗语讲“漂过十二滩，吉祥又平安”。</span></span><span style="font-family:仿宋_GB2312;color:black;"></span> \r\n</p>', 160, '/attached/image/20120903/20120903100439_48.jpg', '十二滩漂流', 1, 3, 1, 19, NULL, NULL);
